#include <stdio.h>
main() {
  int arr[6];
  int max=0, min=99;
  int sum=0;
  for(int i=0; i<6; i++){
    arr[i]=i*i;
    sum+=arr[i];
  }
  for(int i=0; i<6; i++){
    if (max<arr[i])
      max=arr[i];
    if (min>arr[i])
      min=arr[i];
  }
  printf("%.2f", (sum-max-min)/4.0);
}

