main() {
  int arr[5];
  for(int i=0; i<5; i++){
    arr[i]=(i+2)+(i*2);
  }
  for(int i=0; i<5; i++){
    printf("%d", check(arr[i]));
  }
}
int check(int a){
  int n=(int)sqrt(a);
  int i=2;
  while(i<=n){
    if(a%i==0) return 0;
    i++;
  }
  return 1;
}

