#include <stdio.h>
int main() {
  int d = 55;
  int n = 4;
  int r = 0, q = 0;
  r = d;
  while(r >= 4) {
    r = r - n;
    q++;
  }
  printf("%d 그리고 ", q);
  printf("%d", r);
}

class Main {
  public static void main(String[] args) {
    int[] a = {4, 7, 1, 2};
    for(int i=0; i<3; i++) {
      for(int j=i+1; j<4; j++) {
        if(a[i] > a[j]) {
          int temp = a[j];
          a[j] = a[i];
          a[i] = temp;
        }
      }
    }
    for(int i=0; i<4; i++) {
      System.out.print(a[i] + "a");
    }
  }
}

