#include <stdio.h>
#define SIZE 5
int p[4][2] = { {0, 1}, {1, 0}, {0, -1}, {-1, 0} };
int snail(int a[SIZE][SIZE]) {
  int i = 0, j = 0, v = 1, c = 0;
  a[i][j] = v;
  v += 1;
  while (v <= SIZE * SIZE) {
    int ni = i + p[c][0];
    int nj = j + p[c][1];
    if (0 <= ni && ni < SIZE && 0 <= nj && nj < SIZE && a[ni][nj] == 0) {
      i = ni;
      j = nj;
      a[i][j] = v;
      v += 1;
    }
    else {
      c = (c + 1) % ( 가 );
    }
  }
}
int main() {
  int a[SIZE][SIZE] = { 0 };
  snail(a);
  for (int i = 0; i < SIZE; i++) {
    for (int j = 0; j < SIZE; j++) {
      printf("%3d", a[i][j]);
    }
    printf("\n");
  }
}

#include <stdio.h>
int main() {
  int data[] = {15, 12, 26, 23, 31, 36};
  int temp = 0;
  int cnt = sizeof(data)/sizeof(int);
  for(int i=0; i < cnt-1; i++) {
    for(int j=0; j < cnt - 1 - i; j++){
      if(data[j] > data[j+1]) {
        temp = data[j];
        data[j] = data[j+1];
        data[j+1] = temp;
      }
    }
  }
  for(int i=1; i<cnt-1;i++){
    printf("%d ", data[i]);
  }
}

