#include <stdio.h>
#include <string.h>
int main() {
  char* str = "abCDEFgH";
  int count = 0;
  for (int i = 0; i < strlen(str); i++) {
    char ch = str[i];
    if (ch >= 'A' && ch <= 'Z') {
      count++;
    }
  }
  printf("%d", count);
}

