#include <stdio.h>
union Number {
    int intV;
    int floatV;
};

struct data {
    union Number n1;
    union Number n2;
    char isInt;
};

void func(struct data *a) {
    if (a->isInt) {
        a->n1.intV += a->n2.intV;
    }
    else {
        a->n1.floatV += a->n2.floatV;
    }
}

int main() {
    struct data a = {{.intV = 5}, {.floatV = 3.5}, 1};
    func(&a);
    printf("%d", a.n1.intV);
    return 0;
}

