      continue;
    sum += i;
  }
  printf("%d", i + sum);
}

#include <stdio.h>
int factorial(int n, int from, int to, int temp) {
  if(n == 0) {
    return 0;
  }
  else {
    return factorial(n-1, from, temp, to)+1 + factorial(n-1, temp, to, from);
  }
}

int main() {
  int n = 3;
  printf("%d", factorial(n, 3, 2, 1));
  return 0;
}

