public class Main {
  public static void main(String[] args) {
    B a = new B(3);
    B b = new B(3);
    B[] bs = {a, b};
    B temp = bs[0];
    bs[1] = bs[0];
    bs[0] = temp;
    bs[0].a += bs[1].a;
    System.out.print(bs[0].a);
  }
}
class B { int a; B(int a) { this.a = a; } }
